import { useState, useEffect, useRef } from "react";

import { BrowserRouter as Router, useParams } from "react-router-dom";

import ReactPlayer from "react-player/lazy";

import Spinner from "react-bootstrap/Spinner";

import "./css/macbook.css";

const url = "https://product-list-fake-rest-server.herokuapp.com";

export default function MacbookAir({setproductNavStatus, setproductNavContent}) {
  let { id } = useParams();

  const [refSpinnerLoading, setRefSpinnerLoading] = useState(true);

  const [notebookProduct, setNotebookProduct] = useState([]);

  useEffect(() => {
    fetch(`${url}/Notebook/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setproductNavStatus('dark');
        setNotebookProduct(data);
        setproductNavContent(data.name);
        setRefSpinnerLoading(false);
      })
      .catch((error) => console.log(error));
  }, [refSpinnerLoading]);

  return (
    <>
      {refSpinnerLoading === true ? (
        <div style={{height:"100vh"}} className="macbook__air d-flex justify-content-center align-items-center">
          <Spinner className="text-center" style={{color:"white"}} animation="border" role="status">
            <span className="visually-hidden text-center">Loading...</span>
          </Spinner>
        </div>
      ) : (
        <div className="macbook__air">
          <ReactPlayer
            className="air--video"
            url="https://www.apple.com/105/media/us/macbook-air/2020/e3fd8da8-1d68-4e62-886e-7982672a0499/anim/hero/large_2x.mp4"
            width="100%"
            onReady
            playing="true"
          />
          <div className="air--description text-center col-8 mx-auto">
            <h3>
              {notebookProduct.name} <span>({notebookProduct.version})</span>
            </h3>
            <h1>Power. Itâ€™s in the Air.</h1>
            <h4>
              Our thinnest, lightest notebook, completely transformed by the
              Apple M1 chip. CPU speeds up to 3.5x faster. GPU speeds up to 5x
              faster. Our most advanced Neural Engine for up to 9x faster
              machine learning. The longest hours battery life ever in a MacBook Air.
              And a silent, fanless design. This much power has never been this
              ready to go.
            </h4>
          </div>
          <div className="air-product__details ">
            <div className="air-product__wrapper mx-auto d-flex flex-column align-items-center">
            <img alt="" src={notebookProduct.image}></img>
            <h4>From ${notebookProduct.price}</h4>
            <p>{notebookProduct.display}-inch Retina display18</p>
            <p>{notebookProduct.chip}</p>
            <p>Up to {notebookProduct.ram} memory</p>
            <p>Up to {notebookProduct.storage} storage</p>
            <p>Up to {notebookProduct.battery} battery life</p>
            </div>
            </div>
        </div>
      )}
    </>
  );
}
