import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { useEffect, useState } from "react";
import ItemList from "./components/item-list";
import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/esm/Button";
import FormControl from "react-bootstrap/FormControl";
import DropdownButton from "react-bootstrap/DropdownButton";
import { Dropdown } from "bootstrap";


function App() {
  const [notebookProduct, setNotebookProduct] = useState([]);

  const [desktopProduct, setdesktopProduct] = useState();

  const [category, setCategory] = useState("notebook");
  //get data from heroku server
  useEffect(() => {
    fetch("https://product-list-fake-rest-server.herokuapp.com/Notebook")
      .then((response) => response.json())
      .then((data) => {
        setNotebookProduct(data);
      })
      .catch((error) => console.log(error));

    fetch("https://product-list-fake-rest-server.herokuapp.com/Desktop")
      .then((response2) => response2.json())
      .then((data2) => {
        setdesktopProduct(data2);
      })
      .catch((error2) => console.log(error2));
  }, []);

  function searchProduct() {}

  return (
    <div className="container">
      <div className="header row ">
        <h1 className="text-center">Which Mac is right for you?</h1>
        <div className="header__function row">
          <InputGroup className="col-4">
            <Button variant="outline-secondary" id="button-addon1">
              Button
            </Button>
            <FormControl
              aria-label="Example text with button addon"
              aria-describedby="basic-addon1"
            />
          </InputGroup>
          <DropdownButton className="col-4" id="dropdown-basic-button" title="Dropdown button">
            <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
          </DropdownButton>
        </div>
      </div>

      <div className="middle">
        <div className="middle__btn-category row justify-content-center">
          <button
            onClick={() => setCategory("notebook")}
            className="btn--category col-1"
          >
            Notebook
          </button>
          <button
            onClick={() => setCategory("desktop")}
            className="btn--category  col-1"
          >
            Desktop
          </button>
        </div>
        <ItemList
          category={category}
          notebookProduct={notebookProduct}
          desktopProduct={desktopProduct}
        ></ItemList>
      </div>
    </div>
  );
}

export default App;
