import { useState,useEffect } from "react";

import { BrowserRouter as Router, useParams } from "react-router-dom";

import ReactPlayer from "react-player/lazy";

import "./css/macbookAir.css";

const url = "https://product-list-fake-rest-server.herokuapp.com";

export default function MacbookAir() {
  let { id } = useParams();

  const refSpinnerLoading =use

  const [notebookProduct ,setNotebookProduct] =useState([])

  useEffect(()=>{
    fetch(`${url}/Notebook/${id}`)
    .then(response => response.json())
    .then((data) => {
      setNotebookProduct(data);
    })
    .catch((error) => console.log(error));
  },[])


  return (<>
   {refSpinnerLoading.current === true ? (
              <Spinner className="text-center" animation="border" role="status">
                <span className="visually-hidden text-center">Loading...</span>
              </Spinner>
            ) :(
    <div className="macbook__air">
      <ReactPlayer
        className="air--video"
        url="https://www.apple.com/105/media/us/macbook-air/2020/e3fd8da8-1d68-4e62-886e-7982672a0499/anim/hero/medium_2x.mp4"
        width="100%"
        onReady
        playing="true"
      />
      <div className="air--description text-center col-8 mx-auto">
        <h3>{notebookProduct.name} <span>({notebookProduct.version})</span></h3>
        <h1>Power. Itâ€™s in the Air.</h1>
        <h4>Our thinnest, lightest notebook, completely transformed by the Apple M1 chip. CPU speeds up to 3.5x faster. GPU speeds up to 5x faster. Our most advanced Neural Engine for up to 9x faster machine learning. The longest battery life ever in a MacBook Air. And a silent, fanless design. This much power has never been this ready to go.</h4>
      </div>
      <div className="air-product__details">
        {notebookProduct.id}
      </div>
    </div>)}
    </>
  );
}
