import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { useEffect, useRef, useState } from "react";
import ItemList from "./components/item-list";
import PaginationShow from "./components/pagination";

import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/esm/Button";
import FormControl from "react-bootstrap/FormControl";
import DropdownButton from "react-bootstrap/DropdownButton"
import  Dropdown  from "react-bootstrap/Dropdown";
import Spinner from 'react-bootstrap/Spinner'

const url ="https://product-list-fake-rest-server.herokuapp.com";


function App() {
  const [notebookProduct, setNotebookProduct] = useState([]);

  const [desktopProduct, setdesktopProduct] = useState([]);

  const [category, setCategory] = useState("Notebook");

  const [orderSort,setOrderSort] =useState('');

  const [sortCategory,setSortCategory] =useState('');

  const [searchKeyWord,setSearchKeyWord] = useState('');

  const [btnNotebookStatus,setNotebookStatus] = useState('btn-active');

  const refSpinnerLoading = useRef(true);

  const [btnDesktopStatus,setDesktopStatus] = useState()

  const myHeaders = new Headers();

  //get data from heroku server
  useEffect(() => {

    fetch(`${url}/Notebook?_sort=${sortCategory}&_order=${orderSort}&q=${searchKeyWord}&_page=1&_limit=3`,
    )
      .then((response) => {
        console.log(response.headers.get('X-Total-Count'))
        return response.json()})
      .then((data) => {
        setNotebookProduct(data);
        refSpinnerLoading.current=false;
      })
      .catch((error) => console.log(error));

    fetch(`${url}/Desktop?_sort=${sortCategory}&_order=${orderSort}&q=${searchKeyWord}`)
      .then((response2) => response2.json())
      .then((data2) => {
        setdesktopProduct(data2);
        refSpinnerLoading.current=false;
      })
      .catch((error2) => console.log(error2));
  }, [orderSort,searchKeyWord]);

  function changeCategory(category){
    if(category==="Notebook"){
      setNotebookStatus("btn-active");
      setDesktopStatus("btn-nonactive");
    }
    else{
      setNotebookStatus("btn-nonactive");
      setDesktopStatus("btn-active");
    }
    setCategory(category);
  }

  function sortDescending() {
    setSortCategory("price")
    setOrderSort("desc")
  }

  function sortAscending() {
    setSortCategory("price");
   setOrderSort("asc")
  }

  function searchKeyword(keyword){
    if(category==="Notebook"){
      fetch(`${url}/Notebook?q=${keyword}`)
      .then((response) => response.json())
      .then((data) => {
        setNotebookProduct(data);
        refSpinnerLoading.current=false;
      })
      .catch((error) => console.log(error));
    }else{
      fetch(`${url}/Desktop?q=${keyword}`)
      .then((response) => response.json())
      .then((data) => {
        setdesktopProduct(data);
        refSpinnerLoading.current=false;
      })
      .catch((error) => console.log(error));
    }
  }

  console.log(notebookProduct);

  return (
    <div className="container">
      <div className="header row ">
        <h1 style={{marginTop:"30px"}} className="text-center">Which Mac is right for you?</h1>
        <div className="header__function row justify-content-between">
          <InputGroup className="col-4 mb-3">
            <Button onClick={()=>searchKeyword(searchKeyWord)} variant="outline-secondary" id="button-addon1">
              Search
            </Button>
            <FormControl
              onChange={(e)=> setSearchKeyWord(e.target.value)}
              aria-label="Example text with button addon"
              aria-describedby="basic-addon1"
            />
          </InputGroup>
          <DropdownButton className="col-4 text-end" id="dropdown-basic-button" title="Price Filter">
            <Dropdown.Item onClick={()=>sortDescending()}>Desc</Dropdown.Item>
            <Dropdown.Item  onClick={()=>sortAscending()}>Asc</Dropdown.Item>
          </DropdownButton>
        </div>
      </div>

      <div className="middle">
        <div className="middle__btn-category row justify-content-center">
          <button
            onClick={() => changeCategory("Notebook")}
            className={`btn--category ${btnNotebookStatus}  col-lg-1 col-auto`}
          >
            Notebook
          </button>
          <button
            onClick={() => changeCategory("Desktop")}
            className={`btn--category ${btnDesktopStatus}  col-1`}
          >
            Desktop
          </button>
        </div>
        {notebookProduct.length===0 && desktopProduct.length===0?
        (
        <div className="text-center">
          {refSpinnerLoading.current===true?(<Spinner className="text-center" animation="border" role="status">
          <span className="visually-hidden text-center">Loading...</span>
        </Spinner>):<h1 className="text-center">There is no product fit your search</h1>}
        </div>):(<ItemList
          category={category}
          notebookProduct={notebookProduct}
          desktopProduct={desktopProduct}
        ></ItemList>
        )
        }
      </div>
      <div className="footer">
        <PaginationShow></PaginationShow>
      </div>
    </div>
  );
}

export default App;
