import { useState, useEffect, useRef } from "react";

import { BrowserRouter as Router, useParams } from "react-router-dom";

import ReactPlayer from "react-player/lazy";

import Spinner from "react-bootstrap/Spinner";

import "./css/MacPro.css"

const url = "https://product-list-fake-rest-server.herokuapp.com";

export default function MacPro() {
  let { id } = useParams();

  const [refSpinnerLoading, setRefSpinnerLoading] = useState(true);

  const [desktopProduct, setdesktopProduct] = useState([]);

  useEffect(() => {
    fetch(`${url}/Desktop/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setdesktopProduct(data);
        setRefSpinnerLoading(false);
      })
      .catch((error) => console.log(error));
  }, [refSpinnerLoading]);

  return (
    <div className="Mac-pro">
      <div className="Mac-pro__video mx-auto">
          <img width="60%" src="https://www.apple.com/v/imac-27/b/images/overview/hero/hero_hardware__d6dlceuw3nau_large_2x.jpg"></img>
          <ReactPlayer
            className="Mac-pro--video"
            url="https://www.apple.com/105/media/us/imac/2019/cf1a51a9_d9d9_490b_a96e_baeba026f839/anim/hero/large_2x.mp4"
            onReady
            width= "80%"
            playing="true"
          />
      </div>
    </div>
  );
}
