import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const initialState = {
  userName: "",
  passWord: "",
  logInStatus: false,
};

const urlAPI = "https://fake-rest-api-nodejs.herokuapp.com/login";

const data = { username:'', password:''};

export const logIn = createAsyncThunk(
  "authenticate/logIn",
  async () => {
    const response = await fetch(`${urlAPI}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
    if (response.status === 200) {
      const data = await response.json();
      return data
    } else {
      return response.status + ":" + response.statusText;
    }
  }
);

export const authenticateSlide = createSlice({
  name: "authentication",
  initialState,
  reducers: {

    getAccount: (state,action) => {
      if(action.payload[0]!==''&&action.payload[1]!==''){
      state.userName = action.payload[0];
      state.passWord = action.payload[1];
      }
      else{
        console.log('wrong')
      }
      console.log(state.userName,state.passWord)
    },

    checkAccount: (state,action) => {
      // if(action.payload[0]===state.userName&&action.payload[1]===state.passWord){
      //     state.logInStatus=true;
      // }
    },
  },
  extraReducers: (builder) => {
    // Add reducers for additional action types here, and handle loading state as needed
    builder.addCase(logIn.fulfilled, (state, action) => {
      // Add user to the state array
      console.log(action.payload)
      state.userName = action.payload;
    });
  },
});

// Action creators are generated for each case reducer function
export const { getAccount, checkAccount } = authenticateSlide.actions;

export default authenticateSlide.reducer;
