import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getAccount, checkAccount } from "./authenticateSlide";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { InputGroup } from "react-bootstrap";
export default function Authenticate() {
  const [authenStatus, setAuthenStatus] = useState("login");

  const logInStatus = useSelector((state) => state.authenticate.logInStatus);

  const userName = useSelector((state) => state.authenticate.userName);
  const passWord = useSelector((state) => state.authenticate.passWord);

  const [inputUname, setInputUname] = useState("");
  const [inputPassWord, setInputPassWord] = useState("");

  console.log(inputUname, inputPassWord);
  console.log(logInStatus, userName, passWord);

  const [validated, setValidated] = useState(false);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);

    dispatch(getAccount([inputUname, inputPassWord]));
  };  

  const dispatch = useDispatch();

  return (
    <div className="container">
      <button onClick={() => setAuthenStatus("login")}>LogIn</button>
      <button onClick={() => setAuthenStatus("register")}>Register</button>
      {authenStatus === "login" ? (
        <>
          <h1>Logging in </h1>
          <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" />
              <Form.Text className="text-muted">
                We'll never share your email with anyone else.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </>
      ) : (
        <>
          <h1>Regist your account</h1>
          <Form noValidate validated={validated}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <InputGroup hasValidation>
                <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                <Form.Control
                  type="text"
                  placeholder="Username"
                  aria-describedby="inputGroupPrepend"
                  required
                />
                <Form.Control.Feedback type="invalid">
                  Please choose a username.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <InputGroup hasValidation>
                <Form.Control
                  type="text"
                  placeholder="Password"
                  aria-describedby="inputGroupPrepend"
                  required
                />
                <Form.Control.Feedback type="invalid" >
                  Please enter password.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>

            <Button variant="primary" onClick={(e)=>handleSubmit(e)}>
              Submit
            </Button>
          </Form>
        </>
      )}
    </div>
  );
}
