import Pagination from "react-bootstrap/Pagination";

export default function PaginationShow({category,totalCount,ItemsPageLimit}) {
    let totalPagesNeeded=0;
    console.log()
    if(category==="Notebook"){
   totalPagesNeeded= Math.ceil(totalCount.Notebook/ItemsPageLimit);}
  else{
     totalPagesNeeded= Math.ceil(totalCount.Desktop/ItemsPageLimit)
  }
  let active = 1;
  let items = [];
  for (let number = 1; number <= totalPagesNeeded; number++) {
    items.push(
      <Pagination.Item key={number} active={number === active}>
        {number}
      </Pagination.Item>
    );
  }
  return (<><Pagination className="d-flex justify-content-center">{items}</Pagination></>);
}
