import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";
import ItemList from "./components/item-list";
function App() {
  const [notebookProduct,setNotebookProduct] =useState();

  const [desktopProduct,setdesktopProduct] =useState();

  const [catergory,setCategory] = useState("notebook");
  //get data from heroku server
  useEffect(()=>{
    fetch("https://product-list-fake-rest-server.herokuapp.com/Notebook")
    .then((response) =>response.json())
    .then((data) => {
      setNotebookProduct(data);
    })
    .catch((error)=>console.log(error));

    fetch("https://product-list-fake-rest-server.herokuapp.com/Desktop")
    .then((response) =>response.json())
    .then((data) => {
      setdesktopProduct(data);
    })
    .catch((error)=>console.log(error));
  },[])

  return (
    <div className="container">
      <div className="header row ">
        <h1 className="text-center">Which Mac is right for you?</h1>
      </div>
      <div className="middle">
        <div className="middle__btn-category row">
          <button className="col">Notebook</button>
          <button className="col">Desktop</button>
        </div>

      </div>
    </div>
  );
}

export default App;
