import Button from "react-bootstrap/Button";

export default function BagItems({ item }) {
  let formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
  });

  console.log(item);
  return (
    <div className="bag--checkout-item row">
      <div className="checkout-item--image col-3 ">
        <img src={`${item.imageUrl}`} />
      </div>
      <div className="checkout-item--details col-3 text-center">
        <h3 className="mt-4">{item.name}</h3>
      </div>
      <div className="checkout-item--quantity col-3 text-center">
        <h3 className="">{item.quantity}</h3>
        <select class="mt-4 form-select" aria-label="Default select example">
          <option selected>Open this select menu</option>
          <option value="1">One</option>
          <option value="2">Two</option>
          <option value="3">Three</option>
        </select>
      </div>
      <div className="checkout-item--price col-3 text-center d-flex flex-column justify-content-between">
        <h3 className="mt-4">{formatter.format(item.price)}</h3>
        <Button className="col-4 mx-auto">remove</Button>
      </div>
      <hr></hr>
    </div>
  );
}
