import { useEffect, useState } from "react"

import {UseLocalStorageData} from '../LocalStorageContext'

import Spinner from "react-bootstrap/Spinner";

import BagItems from "./bag-item";

import './css/bag.css'

const url = "https://product-list-fake-rest-server.herokuapp.com";

export default function BagItemsList({
    setproductNavStatus,
    setproductNavContent,
  }){

    const [refSpinnerLoading, setRefSpinnerLoading] = useState(true);

    const localStorageItems = UseLocalStorageData();

    const [bagProducts,setBagProducts]=useState();


    let formatter = new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD",
      });


    useEffect(()=>{

        localStorageItems.map(function(item){ 
        async function fetchItems(){
            const refProductsResponse = await Promise.all([fetch(`${url}/${item.category}?id=${item.id}`)])

            const refProducts = await refProductsResponse.json();

            setBagProducts(refProducts);
        }
        })


            .then((response) => response.json())
            .then((data) => {
            data[0].quantity=item.quantity;
            refProducts.push(data[0]);
            console.log("running")
            })
            .catch((error) => console.log(error));

        setRefSpinnerLoading(false);
        setproductNavContent('Bag');

        setproductNavStatus("light");
    },[])


    return(
    <div className="container">
    <div className="bag-list">
        <h1>Review your bag</h1>
        <p>Free delivery and free returns</p>
        <>
        {refSpinnerLoading === true ? (
        <div
          style={{ height: "100vh" }}
          className="Mac-pro d-flex justify-content-center align-items-center"
        >
          <Spinner
            className="text-center"
            style={{ color: "#E0E2E4" }}
            animation="border"
            role="status"
          >
            <span className="visually-hidden text-center">Loading...</span>
          </Spinner>
        </div>
        ):(<div className="bag-item">
            <BagItems bagProducts={bagProducts}></BagItems>
        </div>)
        }
         </>
    </div>
    </div>)

}
