import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { useEffect, useRef, useState } from "react";
import ItemList from "./components/item-list";
import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/esm/Button";
import FormControl from "react-bootstrap/FormControl";
import DropdownButton from "react-bootstrap/DropdownButton"
import  Dropdown  from "react-bootstrap/Dropdown";

const url ="https://product-list-fake-rest-server.herokuapp.com";


function App() {
  const [notebookProduct, setNotebookProduct] = useState([]);

  const [desktopProduct, setdesktopProduct] = useState();

  const [category, setCategory] = useState("Notebook");

  const [orderSort,setOrderSort] =useState('');

  const refSearchKeyWord = useRef('');

  //get data from heroku server
  useEffect(() => {
    fetch(`${url}/Notebook?_sort=price&_order=desc`)
      .then((response) => response.json())
      .then((data) => {
        setNotebookProduct(data);
      })
      .catch((error) => console.log(error));

    fetch(`${url}/Desktop?_sort=price&_order=${orderSort}`)
      .then((response2) => response2.json())
      .then((data2) => {
        setdesktopProduct(data2);
      })
      .catch((error2) => console.log(error2));
  }, []);

  function sortDescending() {
    setOrderSort("desc")
  }

  function sortAscending() {
   setOrderSort("asc")
  }

  function searchKeyword(keyword){
    if(category==="Notebook"){
      fetch(`${url}/Notebook?q=${keyword}`)
      .then((response) => response.json())
      .then((data) => {
        setNotebookProduct(data);
      })
      .catch((error) => console.log(error));
    }else{
      fetch(`${url}/Desktop?q=${keyword}`)
      .then((response) => response.json())
      .then((data) => {
        setdesktopProduct(data);
      })
      .catch((error) => console.log(error));
    }
  }


  return (
    <div className="container">
      <div className="header row ">
        <h1 className="text-center">Which Mac is right for you?</h1>
        <div className="header__function row justify-content-between">
          <InputGroup className="col-4 mb-3">
            <Button onClick={()=>searchKeyword(refSearchKeyWord.current)} variant="outline-secondary" id="button-addon1">
              Search
            </Button>
            <FormControl
              onChange={(e)=> refSearchKeyWord.current=e.target.value}
              aria-label="Example text with button addon"
              aria-describedby="basic-addon1"
            />
          </InputGroup>
          <DropdownButton className="col-4 text-end" id="dropdown-basic-button" title="Price Filter">
            <Dropdown.Item onClick={()=>sortDescending()}>Desc</Dropdown.Item>
            <Dropdown.Item  onClick={()=>sortAscending()}>Asc</Dropdown.Item>
          </DropdownButton>
        </div>
      </div>

      <div className="middle">
        <div className="middle__btn-category row justify-content-center">
          <button
            onClick={() => setCategory("Notebook")}
            className="btn--category col-1"
          >
            Notebook
          </button>
          <button
            onClick={() => setCategory("Desktop")}
            className="btn--category  col-1"
          >
            Desktop
          </button>
        </div>
        <ItemList
          category={category}
          notebookProduct={notebookProduct}
          desktopProduct={desktopProduct}
        ></ItemList>
      </div>
    </div>
  );
}

export default App;
