import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { useEffect, useState } from "react";
import ItemList from "./components/item-list"
import InputGroup from 'react-bootstrap/InputGroup'
import Button from "react-bootstrap/esm/Button";
import FormControl from "react-bootstrap"


function App() {
  const [notebookProduct,setNotebookProduct] =useState([]);

  const [desktopProduct,setdesktopProduct] =useState();

  const [category,setCategory] = useState("notebook");
  //get data from heroku server
  useEffect(()=>{
    fetch("https://product-list-fake-rest-server.herokuapp.com/Notebook")
    .then((response) =>response.json())
    .then((data) => {
      setNotebookProduct(data);
    })
    .catch((error)=>console.log(error));

    fetch("https://product-list-fake-rest-server.herokuapp.com/Desktop")
    .then((response2) =>response2.json())
    .then((data2) => {
      setdesktopProduct(data2);
    })
    .catch((error2)=>console.log(error2));
  },[])

  function searchProduct(){

  }

  return (
    <div className="container">
      <div className="header row ">
        <h1 className="text-center">Which Mac is right for you?</h1>
        <div className="header__function">
        <InputGroup className="mb-3">
    <Button variant="outline-secondary" id="button-addon1">
      Button
    </Button>
    <FormControl
      aria-label="Example text with button addon"
      aria-describedby="basic-addon1"
    />
  </InputGroup>
        </div>
      </div>

      <div className="middle">
        <div className="middle__btn-category row justify-content-center">
          <button onClick={()=>setCategory("notebook")} className="btn--category col-1">Notebook</button>
          <button onClick={()=>setCategory("desktop")} className="btn--category  col-1">Desktop</button>
        </div>
        <ItemList category={category} notebookProduct={notebookProduct} desktopProduct={desktopProduct}></ItemList>
      </div>
    </div>
  );
}

export default App;
