import { useState, useEffect, useRef } from "react";

import { BrowserRouter as Router, useParams } from "react-router-dom";

import ReactPlayer from "react-player/lazy";

import Spinner from "react-bootstrap/Spinner";

import "./css/MacPro.css"

const url = "https://product-list-fake-rest-server.herokuapp.com";

export default function MacPro() {
  let { id } = useParams();

  const [refSpinnerLoading, setRefSpinnerLoading] = useState(true);

  const [desktopProduct, setdesktopProduct] = useState([]);

  useEffect(() => {
    fetch(`${url}/Desktop/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setdesktopProduct(data);
        setRefSpinnerLoading(false);
      })
      .catch((error) => console.log(error));
  }, [refSpinnerLoading]);

  return (
    <>
    {refSpinnerLoading === true ? (
      <div style={{height:"100vh"}} className="Mac-pro d-flex justify-content-center align-items-center">
        <Spinner className="text-center" style={{color:"#E0E2E4"}} animation="border" role="status">
          <span className="visually-hidden text-center">Loading...</span>
        </Spinner>
      </div>
    ):
    (<>
    <div className="Mac-pro">
        <h1>{desktopProduct.name}<span>({desktopProduct.version})</span></h1>
      <div className="Mac-pro__intro mx-auto d-flex justify-content-center">
          <img className="Mac-pro__image" width="60%" src="https://www.apple.com/v/imac-24/b/images/overview/colors_hero_fallback__co1mroy32piu_large.jpg"></img>
      </div>
      <h2 className="text-center col-5 mx-auto"><span>Say</span> <span>hello</span>  to the new iMac.<br></br>
Inspired by the best of Apple. Transformed by the M1 chip. Stands out in any space. Fits perfectly into your life.</h2>
    </div>
    <div className="Mac-pro__details">

    </div>
    </>
    )}
    </>
  );
}
