import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getAccount, checkAccount } from "./authenticateSlide";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { InputGroup } from "react-bootstrap";

import './authenticate.css'
import Home from "../../home";

export default function Authenticate({setproductNavStatus,setproductNavContent}) {
  const [authenStatus, setAuthenStatus] = useState("login");

  const logInStatus = useSelector((state) => state.authenticate.logInStatus);

  const userName = useSelector((state) => state.authenticate.userName);
  const passWord = useSelector((state) => state.authenticate.passWord);

  const [inputUname, setInputUname] = useState("");
  const [inputPassWord, setInputPassWord] = useState("");

  console.log(inputUname, inputPassWord);
  console.log(logInStatus, userName, passWord);

  const [validated, setValidated] = useState(false);

  useEffect(() => {
        setproductNavStatus("light");
        setproductNavContent('Authenticate');

  }, []);


  const handleSubmitRegister = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);

    dispatch(getAccount([inputUname, inputPassWord]));
  };

  const handleSubmitLogIn = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);

    dispatch(checkAccount([inputUname, inputPassWord]));
  };

  const dispatch = useDispatch();

  return (
    <>
      {logInStatus === true ? (
        // <Home></Home>
        <h1>Logged</h1>
      ) : (
        <div className="container authenticate">

          {authenStatus === "login" ? (
            <>
              <h1 className='mb-4'>Please sign in </h1>
              <Form className='col-sm-6 col-12 mb-4' noValidate validated={validated}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <InputGroup hasValidation >
                    <Form.Control
                      size="lg"
                      type="email"
                      placeholder="Username"
                      aria-describedby="inputGroupPrepend"
                      required
                      onChange={(e) => setInputUname(e.target.value)}
                    />
                    <Form.Control.Feedback type="invalid">
                      Invalid username.
                    </Form.Control.Feedback>
                  </InputGroup>
                </Form.Group>

                <Form.Group className="mb-3" controlId="LogInPassword">
                  <InputGroup hasValidation>
                    <Form.Control
                      size="lg"
                      type="text"
                      placeholder="Password"
                      aria-describedby="inputGroupPrepend"
                      required
                      onChange={(e) => setInputPassWord(e.target.value)}
                    />
                    <Form.Control.Feedback type="invalid">
                      Invalid password.
                    </Form.Control.Feedback>
                  </InputGroup>
                </Form.Group>

                <Button variant="primary" onClick={(e) => handleSubmitLogIn(e)}>
                  Submit
                </Button>
              </Form>
            </>
          ) : (
            <>
              <h1 className='mb-4'>Regist your account</h1>
              <Form className='col-sm-6 col-12 mb-4' noValidate validated={validated}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <InputGroup hasValidation>
                    <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                    <Form.Control
                      size="lg"
                      type="email"
                      placeholder="Username"
                      aria-describedby="inputGroupPrepend"
                      required
                      onChange={(e) => setInputUname(e.target.value)}
                    />
                    <Form.Control.Feedback type="invalid">
                      Please choose a username.
                    </Form.Control.Feedback>
                  </InputGroup>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <InputGroup hasValidation>
                    <Form.Control
                      size="lg"
                      type="text"
                      placeholder="Password"
                      aria-describedby="inputGroupPrepend"
                      required
                      onChange={(e) => setInputPassWord(e.target.value)}
                    />
                    <Form.Control.Feedback type="invalid">
                      Please enter password.
                    </Form.Control.Feedback>
                  </InputGroup>
                </Form.Group>

                <Button
                  variant="primary"
                  onClick={(e) => handleSubmitRegister(e)}
                >
                  Submit
                </Button>
              </Form>
            </>
          )}
           <button className='btn-' onClick={() => setAuthenStatus("login")}>LogIn</button>
          <button onClick={() => setAuthenStatus("register")}>Register</button>
        </div>

      )
      }

    </>
  );
}
