import Button from "react-bootstrap/Button";
import {IoHardwareChipOutline} from "react-icons/io5";
import {SiIntel} from "react-icons/si"
const standardNotebookDisplay = 13;
const M1Chip = "Apple M1 chip";

export default function ItemShow({ product }) {
  return (
    <div className="item col-12 col-lg-4 col-sm-6 text-center">
      <img src={product.image}></img>
      <h4>{product.name}</h4>
      <p>from {product.price}$</p>
      <Button size="sm">buy</Button>
      <hr />
      {product.display != "" ? (
        <div className="item--display">
          {product.display >= standardNotebookDisplay ? (
            <h5>{product.display}"</h5>
          ) : (
            <h5>{product.display}K</h5>
          )}
          <p>
            Retina display <br></br> <span>218ppi</span>
          </p>
        </div>
      ) : (
        <div className="item--display">
          <h5>-</h5>
        </div>
      )}
      {product.chip === M1Chip ? (
        <div className="titem__chip--M1">
            <IoHardwareChipOutline></IoHardwareChipOutline>
          <p>{product.chip}</p>
        </div>
      ) : (
        <div className="item__chip--intel">
            <SiIntel></SiIntel>
          <p>Up to {product.chip}</p>
        </div>
      )}

      <h5>{product.ram}</h5>
      <h4>{product.storage}</h4>
      <h4>{product.battery}</h4>
      <h4>{product.weight}</h4>
    </div>
  );
}
